INSERT INTO lms.tests (id, title, description, type, time_limit_in_seconds, start_date, due_date, content, created_at, updated_at) VALUES (1, 'Programming Exam', 'For your first exam, I prepared these items for you to ponder. If you are about to answer this activity, I assume that youâ€™re already done reading the material I uploaded under Modules 1 and 2. How was it going so far? Another piece of a cake, right? Please answer the following, briefly and straightforward. This quiz should be done individually.', 'exam', 900, '2021-05-01 00:00:00', '2021-07-03 00:00:00', '[{"type": "multiple-choice", "label": "In programming languages, what does PHP stand for", "answer": "php1", "number": 1, "options": [{"id": "php1", "label": "PHP: Hypertext Preprocessor"}, {"id": "php2", "label": "Programming Hypertext Processor"}, {"id": "php3", "label": "Perl Hypertext Program"}]}, {"type": "multiple-choice", "label": "Which of the following is not a database software?", "answer": "tomcat", "number": 2, "options": [{"id": "postgres", "label": "PostgreSQL"}, {"id": "mysql", "label": "MySQL"}, {"id": "maria", "label": "MariaDB"}, {"id": "tomcat", "label": "Tomcat"}]}, {"type": "multiple-choice", "label": "PHP is a compiled language. True or False?", "answer": "false", "number": 3, "options": [{"id": "true", "label": "True"}, {"id": "false", "label": "False"}]}, {"type": "multiple-choice", "label": "What type of server is used to host PHP applications?", "answer": "web", "number": 4, "options": [{"id": "file", "label": "File Server"}, {"id": "web", "label": "Web Server"}, {"id": "db", "label": "Database Server"}]}, {"type": "multiple-choice", "label": "Which is not a relational database?", "answer": "mongo", "number": 5, "options": [{"id": "sql", "label": "SQL Server"}, {"id": "db2", "label": "IBM DB2"}, {"id": "mongo", "label": "Mongo DB"}, {"id": "mysql", "label": "MySQL"}]}, {"type": "multiple-choice", "label": "Javascript can only run in the client side on web browsers. True or False?", "answer": "false", "number": 6, "options": [{"id": "true", "label": "True"}, {"id": "false", "label": "False"}]}, {"type": "multiple-choice", "label": "PHP is a weakly-typed language. True or False?", "answer": "true", "number": 7, "options": [{"id": "true", "label": "True"}, {"id": "false", "label": "False"}]}, {"type": "multiple-choice", "label": "What is the default MySQL port number?", "answer": "3306", "number": 8, "options": [{"id": "1521", "label": "1521"}, {"id": "5432", "label": "5432"}, {"id": "1433", "label": "1433"}, {"id": "3306", "label": "3306"}]}, {"type": "multiple-choice", "label": "Which of the following cannot be utilized when building PHP applications?", "answer": "java", "number": 9, "options": [{"id": "html", "label": "HTML"}, {"id": "css", "label": "CSS"}, {"id": "javascript", "label": "Javascript"}, {"id": "java", "label": "Java"}]}, {"type": "multiple-choice", "label": "Which of the following indicates a success response code?", "answer": "200", "number": 10, "options": [{"id": "404", "label": "404"}, {"id": "403", "label": "403"}, {"id": "200", "label": "200"}, {"id": "500", "label": "500"}]}]', '2021-04-27 23:18:16', '2021-04-27 23:18:18');
